INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('6ab1cb3c-821b-4953-b0d5-ece4f6923cc5', '24402', 'Emmanuelle', 'Hồi Ký Emmanuelle', 'Phim xoay quanh cô nàng Emmanuelle, vốn là một người mẫu trẻ đẹp và sống ở Bangkok cùng với chồng Jean của cô, một người đàn ông khá nhiều tuổi, tất nhiên là nhiều hơn cô. Cô ấy thích anh ấy vì anh ấy đã dạy cô ấy rất nhiều điều, và anh ấy thích cô ấy vì khả năng tiếp thu của cô ấy rất tốt, cô ấy cũng luôn muốn học những điều mới hay nói cách khác là một người ham học. Cả hai đều rất khoan dung trong những vấn đề ngoại tình, vì vậy anh ta không quan tâm đến một Marie-Ange trẻ tuổi đến nhiều lần, mặc dù rõ ràng cô ấy muốn nhiều hơn là nói chuyện với vợ mình. Nhưng Emmanuelle lại thích thú với Bee già nua hơn và tham gia vào chuyến đi của cô ấy ở trong rừng. Mối quan hệ của 2 vợ chồng vẫn tốt đẹp mặt cho việc họ công khai ngoại tình để tự tìm khám phá bản thân...', 'https://image.tmdb.org/t/p/original/aUYDbpjBtn7BaRokrzSJZbByJtl.jpg', 'https://image.tmdb.org/t/p/original/k5qXRqhCkzETnH0ktCtyBGR7pWz.jpg', '1974-06-25', '95', '6.0', 'Phim Chính Kịch, Phim Lãng Mạn', 'null', 'false', 'beginner', '2025-12-06 13:52:27.616175+00');
INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('af2e4509-3280-45ec-8230-62a83382a0b5', '370172', 'No Time to Die', 'Điệp Viên 007: Không Phải Lúc Chết', 'Phần 25 của bộ phim điệp viên huyền thoại 007 sẽ tiếp nối câu chuyện về James Bond cùng những pha hành động táo bạo và hoành tráng hơn bao giờ hết. Sau sự kiện đầy ám ảnh trong Spectre, Bond lui về ở ẩn tại đất nước Jamaica, sống một cuộc đời cô độc nhưng bình lặng. Bỗng một người bạn cũ từ CIA xuất hiện, cầu xin anh giúp đỡ. Bond bất đắc dĩ phải tái xuất, nhưng không hề biết mình sẽ đối đầu với thế lực nào. Chi tiết đáng chú ý nhất là chiếc mặt nạ trắng vỡ nửa, đánh dấu sự xuất hiện của tên ác nhân kì quái bậc nhất trong cả series 007. Màn chạm trán giữa Bond và kẻ thù nguy hiểm này sẽ vén màn những bí ẩn còn để ngỏ và tiếp theo đó, có thể là một cuộc đối đầu “sinh tử”.', 'https://image.tmdb.org/t/p/original/5w7laXsajb3qATS8qwvf0RaJJNc.jpg', 'https://image.tmdb.org/t/p/original/x9td0NQha0t44K2qgcMipLOGjVi.jpg', '2021-09-29', '163', '7.4', 'Phim Hành Động, Phim Gây Cấn, Phim Phiêu Lưu', 'null', 'false', 'beginner', '2025-12-06 13:49:56.268098+00');
