INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('82ba70f1-0a67-4d7e-8e1a-cbae3dc98a17', '38199', 'Unthinkable', 'Vụ Khủng Bố Không Tưởng', 'Unthinkable lấy bối cảnh nước Mỹ bị đe dọa bởi 3 quả bom nguyên tử được giấu ở 3 thành phố lớn. 3 quả bom được hẹn giờ cho nổ vào thứ Sáu. Thủ phạm đã bị bắt vào thứ Tư. Và việc tiếp theo, là buộc thủ phạm nói ra nơi giấu 3 quả bom. Nội dung phim xoay quanh việc tra hỏi thủ phạm để tìm ra vị trí 3 quả bom. Trong phim, khi đối diện với kẻ thù, nước Mỹ đã từng bước từng bước phải từ bỏ các giá trị về nhân quyền của mình, những giá trị Mỹ, điều làm nên nền tảng của Giấc Mơ Mỹ. Những giá trị về nhân quyền, về thượng tôn pháp luật, những điều mà Mỹ tự hào dường như bị chính nước Mỹ chối bỏ khi phải đối mặt với nguy hiểm.', 'https://image.tmdb.org/t/p/original/6yQqguytl10FhImngDHV90Aqewa.jpg', 'https://image.tmdb.org/t/p/original/p80dRTtTQE5U7FeDoJXanxFX2xO.jpg', '2010-05-26', '97', '6.8', 'Phim Chính Kịch, Phim Gây Cấn', 'null', 'false', 'beginner', '2025-12-06 13:47:47.932325+00');
