INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('81d05397-b753-449f-8a79-e6ea8d6dce11', '271110', 'Captain America: Civil War', 'Captain America: Nội Chiến Siêu Anh Hùng', 'Captain America: Civil War (Nội Chiến Siêu Anh Hùng) xoay quanh việc Steve Rogers (Captain America) dẫn đầu một nhóm siêu anh hùng với các thành viên của Avengers với mục tiêu tiếp tục nỗ lực để bảo vệ nhân loại. Nhưng sau nhiều vụ việc xảy ra do biệt đội Avengers làm thiệt hại tài sản, chính phủ đã thiết lập cơ quan quản lý và giám sát nhóm. Điều này gây ra sự chia rẽ trong nội bộ nhóm Avengers, tạo nên hai phe đối đầu nhau. Một bên được lãnh đạo bởi Steve Rogers (Captain America) với mong muốn duy trì nhóm để thực hiện mục tiêu bảo vệ nhân loại mà không có sự can thiệp của chính phủ. Phe còn lại là của Tony Stark (Iron Man) luôn hỗ trợ giám sát và có trách nhiệm giải trình mọi việc làm với chính phủ.', 'https://image.tmdb.org/t/p/original/hDTVGIG8jsa1ZwO3wV0ccvDN4ZH.jpg', 'https://image.tmdb.org/t/p/original/kvRT3GwcnqGHzPjXIVrVPhUix7Z.jpg', '2016-04-27', '147', '7.4', 'Phim Phiêu Lưu, Phim Hành Động, Phim Khoa Học Viễn Tưởng', 'null', 'false', 'beginner', '2025-12-06 13:45:09.444262+00');
INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('e04419d8-fa59-4934-be97-cdacf5f23702', '24', 'Kill Bill: Vol. 1', 'Cô Dâu Báo Thù 1', '"Nhân vật chính của bộ phim được gọi là Cô Dâu (Uma Thurman)', 'là một thành viên của Tổ chức Ám Sát Vipe', 'dẫn đầu bởi Bill', 'người yêu của cô. Khi nhận ra mình mang thai đứa con của Bill', 'Cô Dâu đã quyết định rời bỏ cuộc sống của một kẻ ám sát', 'giết người. Cô đến Texas', 'gặp một người đàn ông trẻ', 'và quyết định sống cuộc sống mới. Nhưng vào ngày cưới', 'Bill đã xuất hiện cùng với 4 sát thủ còn lại giết hết tất cả mọi người. Nhưng họ đã sơ sót', 'Cô Dâu vẫn còn sống và chìm trong hôn mê suốt 4 năm trời. Tỉnh dậy', 'nhớ lại tất cả những gì đã xảy ra');
