INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('fe3991a0-9db3-41a0-b81b-a0d56cc13add', '7485', 'Shooter', 'Thiện Xạ', 'Xạ thủ bắn tỉa cừ khôi nhất quân đội Mỹ được mời ngăn chặn âm mưu ám sát tổng thống. Khi biết đó chỉ là một cái bẫy, anh phải đơn thương độc mã chống lại những chính trị gia biến chất để minh oan cho mình.Bob Lee Swagger (Mark Wahlberg) là một lính bắn tỉa thuộc lực lượng lính thủy đánh bộ Mỹ. Với tài bắn tỉa trăm phát trăm trúng ở khoảng cách xa, anh nổi danh trong tất cả binh chủng với biệt danh “Bob cừ khôi”. Sau khi mất người bạn thân, cũng là đồng đội, trong một chiến dịch ở Ethiopia, Bob quay trở về bang Wyoming và xin rời ngũ. Quá đau buồn trước cái chết của bạn, anh dựng một ngôi nhà nhỏ trên núi và lên đó sống cùng một con chó săn. Anh chỉ liên hệ với thế giới bên ngoài thông qua mạng Internet.36 tháng đã trôi ...', 'https://image.tmdb.org/t/p/original/kgDJroNcTagDObZwBAHO9k0m6Ga.jpg', 'https://image.tmdb.org/t/p/original/uy2trYgO8MOQfQXlvMaLhBnkUqg.jpg', '2007-03-22', '124', '7.1', 'Phim Chính Kịch, Phim Hành Động, Phim Gây Cấn', 'null', 'false', 'beginner', '2025-12-06 13:49:00.573963+00');
INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('5f252a39-e8c5-4bb0-9a7c-9a927e63ec64', '652', 'Troy', 'Người Hùng Thành Troy', 'Trong suốt các thời đại, đàn ông đã gây chiến. Một số cho quyền lực, một số cho vinh quang hoặc danh dự, và một số cho tình yêu. Ở Hy Lạp cổ đại, niềm đam mê của hai người tình huyền thoại nhất trong lịch sử là Paris, Hoàng tử thành Troia (Orlando Bloom) và Helen (Diane Kruger), Nữ hoàng Sparta, đã mở ra một cuộc chiến tranh tàn phá nền văn minh. Vụ bắt cóc Helen bởi Paris, tách cô khỏi chồng mình, Vua Menelaus (Bren-dan Gleeson), là một sự xúc phạm mà Family Pride cho rằng đối mặt với Menelaus là một mối quan hệ với anh trai của cô là Agamemnon (Brian Cox), vị vua quyền lực của cô Mycenae, người sớm tái hợp tất cả các bộ lạc lớn của Hy Lạp để phục hồi Helen khỏi Trojans và bảo vệ danh dự của anh trai cô. Sự thật là cuộc chiến vì danh dự của Agamemnon bị hủy hoại bởi lòng tham không thể kiểm soát của anh ta khi anh ta cần phải chinh phục thành Troia để giành quyền kiểm soát Biển Aegean.', 'https://image.tmdb.org/t/p/original/a07wLy4ONfpsjnBqMwhlWTJTcm.jpg', 'https://image.tmdb.org/t/p/original/d3z8MH9OvTOOSxy5QwAG0cS6GKU.jpg', '2004-05-13', '163', '7.2', 'Phim Chiến Tranh, Phim Hành Động, Phim Lịch Sử', 'null', 'false', 'beginner', '2025-12-06 13:43:42.289335+00');
