INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('907e3a56-3914-493f-9f58-5bc567cfc9dc', '920', 'Cars', 'Vương Quốc Xe Hơi', '"McQueen Tia chớp khoác trên mình lớp sơn đỏ rực', 'rất sung sức', 'tự mãn và thường trực nụ cười mỉa mai. Lao vun vút trên đường 66 tới California để tranh cúp Piston', 'cậu đã đâm sầm vào thị trấn Radiator Springs buồn tẻ', 'phá nát đường đi và suýt bị ngồi tù. Chỉ vài ngày nữa', 'anh chàng phải tham gia cuộc đua', 'mong được đại gia Dinoco tài trợ để trở thành sao. Nhờ sáng kiến của cô nàng luật sư Sally - một chiếc Porsche bóng bẩy đời 2002 - McQueen phải đem thân hình công tử ra kéo chiếc xe rải nhựa khổng lồ để làm lại đường. Cậu làm quen với bác sĩ Hudson', 'một chiếc Hudson Hornet đời 1951 từng là huyền thoại của đường đua Piston. Ngoài ra còn có Mater chiếc xe tải cũ mèm', 'gỉ sét và vui tính. Từ thị trấn nhỏ bé này', 'McQueen đã nhận ra nhiều điều ý nghĩa trong cuộc sống."', 'https://image.tmdb.org/t/p/original/6VIxYR9dYeSgBqoR95fk1jnccRy.jpg');
INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('a08e19dc-9125-45f0-98b6-c7ddb850f937', '378064', '映画 聲の形', 'Dáng Hình Thanh Âm', 'Nishimiya Shouko, một cô bé bị điếc từ nhỏ, đã chuyển trường vào lớp của cậu bé Ishida Shouya. Để thỏa mãn sự tò mò của mình, cậu bé Shouya đã khơi mào trò bắt nạt Shouko vì tật khiếm thính. Trong một lần bắt nạt quá đà, Shouko đã bị chảy máu tai, và sau đó cô bé phải tiếp tục chuyển trường. Shouya, thay cho Shouko, bị cả lớp tẩy chay và bắt nạt. Mãi cho tới khi bước vào cao trung, Shouya, bị hành hạ và ân hận bởi những gì mình đã làm trong quá khứ, quyết định rằng cậu phải gặp lại Shouko một lần nữa. Shouya muốn bù đắp lại những tổn thương mà cậu đã gây ra cho Shouko và thực lòng muốn làm bạn với cô bé.', 'https://image.tmdb.org/t/p/original/aqri8up06zMaCn0oyYcZporiwj3.jpg', 'https://image.tmdb.org/t/p/original/5lAMQMWpXMsirvtLLvW7cJgEPkU.jpg', '2016-09-17', '130', '8.4', 'Phim Hoạt Hình, Phim Chính Kịch, Phim Lãng Mạn', 'null', 'false', 'beginner', '2025-12-06 13:49:49.407635+00');
