INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('0793d9f1-d89e-42da-89ec-bd917119dd2f', '9659', 'Mad Max', 'Max Điên 1', 'Khi nhân loại bước vào thế kỷ 21, Australia rơi vào tình trạng hỗn loạn bởi sự thiếu hụt dầu mỏ. Kinh tế suy thoái khiến tỷ lệ thất nghiệp tăng lên, đẩy nhiều thanh niên vào con đường trộm cướp.  Tại một vùng nông thôn rộng lớn của Autralia, cuộc sống của người dân ngày càng trở nên khốn quẫn bởi sự hoành hành của một băng cướp đi xe máy. Max Rockatansky là một trong những cảnh sát xuất sắc nhất trong việc truy bắt bọn cướp nhờ khả năng bắn súng và lái xe điêu luyện.  Max Trong 1 lần rượt đuổi tốc độ cao Max đã khiến 1 tên cướp mất mạng và cuộc sống của Max cũng bị hỗn loạn khi đồng bọn của tên cướp kia quyết tâm trả thù.', 'https://image.tmdb.org/t/p/original/5LrI4GiCSrChgkdskVZiwv643Kg.jpg', 'https://image.tmdb.org/t/p/original/8CcjyTF3hXIe2wRdFkv52zkOmO1.jpg', '1979-04-12', '91', '6.7', 'Phim Phiêu Lưu, Phim Hành Động, Phim Gây Cấn, Phim Khoa Học Viễn Tưởng', 'null', 'false', 'beginner', '2025-12-06 13:52:16.151502+00');
