INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('e0f585f8-ac89-43fb-8a08-1c7ad6105f5d', '429', 'Il buono, il brutto, il cattivo', 'Thiện, Ác, Tà', 'Thiện, Ác, Tà (The Good, The Bad And The Ugly): Blondie là tay cao bồi chuyên săn tiền thưởng ở miền Tây. Tuco là tên tội phạm đang lẩn trốn sự truy đuổi. Cả hai hợp tác kiếm tiền từ lệnh truy nã của Tuco. Cả hai phát hiện ra một kho vàng khi chạm trán đoàn xe bị cướp và lấy được thông tin từ kẻ sống sót cuối cùng. Tuco đã kịp nghe được tên của nghĩa địa chôn vàng, còn Blondie nhớ được tên ngôi mộ... Giờ đây, cả hai phải giữ mạng sống cho kẻ còn lại nếu muốn tìm được kho vàng. Cũng trong lúc đó, tên sát thủ Angel Eye đang trên đường tới nơi chôn giấu...', 'https://image.tmdb.org/t/p/original/hTPEtEMiYFJ4lHXsay57tZX673Q.jpg', 'https://image.tmdb.org/t/p/original/Adrip2Jqzw56KeuV2nAxucKMNXA.jpg', '1966-12-22', '161', '8.5', 'Phim Miền Tây', 'null', 'false', 'beginner', '2025-12-06 13:46:00.952105+00');
INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('8ba0749f-9f98-4c12-805b-9fec7890fb8a', '107', 'Snatch', 'Tên Trộm Nữ Trang', 'Snatch mang phong thái giống như Pulp Fiction (1994) của Quentin Tarantino. Phim xoay quanh những câu chuyện, những hành động, những toan tính, âm mưu của hai nhóm tội phạm. Tất cả đều rất bình thường, xảy ra trong đời sống hàng ngày, không có gì to tát, ẩn ý hay có tính tuyên ngôn này kia. Cái khác của Guy Ritchie trong Snatch là những tình huống hài nhẹ, tự nhiên nảy sinh ở từng hành động của các nhân vật. Điều này khiến cho Snatch giảm bớt được sự căng thẳng và tạo được thêm sự thích thú ở khán giả so với Pulp Fiction (1994). Một bộ phim về tội phạm với phong cách tưng tửng, hài hước, không có cốt truyện nhưng lại rất hấp dẫn. Trang đánh giá phim trực tuyến IMDb đã xếp hạng Snatch đứng thứ 103 trong Top 250.', 'https://image.tmdb.org/t/p/original/kJZoAHq1SLDdWjeNGtlHAnGpmFV.jpg', 'https://image.tmdb.org/t/p/original/gTghF8ardwJ48XLHkiapWk3ZmSo.jpg', '2000-09-01', '103', '7.8', 'Phim Hình Sự, Phim Hài', 'null', 'false', 'beginner', '2025-12-06 13:49:36.266677+00');
