INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('56bf2ffa-f433-41ad-a7ec-7b2961e90b8c', '1185528', '射雕英雄传：侠之大者', 'Anh Hùng Xạ Điêu: Hiệp Chi Đại Giả', 'Trong giang hồ đầy ân oán tình thù, giữa thời đại chiến loạn quyền lực tranh giành, Quách Tĩnh (do Tiêu Chiến thủ vai) từ nhỏ đã rời xa quê hương, dần rèn luyện nên một sức mạnh to lớn có thể thay đổi cả cục diện và vận mệnh. Dù được các cao nhân xem trọng và truyền dạy những tuyệt học võ công như “Cửu Âm Chân Kinh” và “Giáng Long Thập Bát Chưởng”, nhưng chính điều đó cũng khiến anh trở thành mục tiêu đố kỵ của nhiều thế lực. Quách Tĩnh, khiêm nhường nhưng kiên định, giữ trọn tấm lòng trong sáng. Cùng Hoàng Dung (do Trang Đạt Phi thủ vai), anh dũng cảm xông pha giữa khói lửa chiến trường và mưa tên ngựa sắt, gánh vác sứ mệnh bảo vệ biên ải nhà Nam Tống. Giữa biển trời và chim điêu, người anh hùng không biết sợ hãi – người đại hiệp chân chính, xoay chuyển cả giang sơn, lập nên truyền thuyết võ lâm.', 'https://image.tmdb.org/t/p/original/dLnf9qxJt7bQcx2q5hI9MmZCJwT.jpg', 'https://image.tmdb.org/t/p/original/1DTIRhw4cpLJlHlrPPbKzq6amHc.jpg', '2025-01-29', '147', '6.9', 'Phim Hành Động, Phim Chính Kịch, Phim Lịch Sử', 'null', 'false', 'beginner', '2025-12-06 13:45:18.657489+00');
INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('20778576-0860-4c2b-913a-026338c09d89', '9952', 'Rescue Dawn', 'Giải Cứu Lúc Bình Minh', 'Rescue Dawn (2006) có tựa đề tiếng Việt là Giải Cứu Lúc Bình Minh. Đây là một bộ phim chiến tranh - phiêu lưu do Werner Herzog đạo diễn, kể về hành trình sinh tồn của Dieter Dengler, một phi công Mỹ bị bắn rơi và bắt giữ trong Chiến tranh Việt Nam. Christian Bale vào vai Dengler, thể hiện sự kiên cường và quyết tâm của nhân vật khi phải đối mặt với điều kiện khắc nghiệt trong trại tù và tìm cách trốn thoát.', 'https://image.tmdb.org/t/p/original/ymPlV2ymUBSctfRzpehFOxFmJiS.jpg', 'https://image.tmdb.org/t/p/original/q4RamDqx4kXCrUvk49o4FmVBbb.jpg', '2007-06-23', '125', '7.0', 'Phim Phiêu Lưu, Phim Chính Kịch, Phim Chiến Tranh', 'null', 'false', 'beginner', '2025-12-06 13:53:43.29313+00');
