INSERT INTO "movies" ("id", "tmdb_id", "title", "title_vi", "overview", "poster", "background_image", "release_date", "runtime", "vote_average", "genres", "video_url", "is_vip", "difficulty_level", "created_at") VALUES ('d0859fd0-0fc5-4e46-a89e-ef340e058139', '204082', 'Homefront', 'Bước Đường Cùng', 'Homefront được xây dựng trên hình mẫu những bộ phim về miền viễn tây kinh điển, với cốt truyện xoay quanh một cựu sĩ quan cảnh sát sống cuộc sống bình lặng tại một thị trấn nhỏ, trước khi nhận ra mình không thể chạy trốn khỏi quá khứ. Phil Broker, nhân vật của Jason Statham, đã quyết định chuyển về vùng nông thôn Louisiana sau một chiến dịch thất bại. Tất cả những gì anh còn là cô con gái… sau khi người vợ qua đời. Đau đớn, cô độc, anh chọn sống tách biệt cho tới khi con gái anh vô tình gây mâu thuẫn với một gia đình địa phương cùng sống trong thị trấn nhỏ ở nhánh sông phía nam này. Mâu thuẫn nhỏ đó cứ leo thang, leo thang và ngày càng trầm trọng hơn, cho tới khi vỏ bọc của anh đột ngột bị lật tẩy.', 'https://image.tmdb.org/t/p/original/qqR1M5BgvKjoKBLbi8PjhymGaKc.jpg', 'https://image.tmdb.org/t/p/original/kT8orKwz2bmRdHBLnG44yucYtFy.jpg', '2013-11-12', '100', '6.9', 'Phim Hành Động, Phim Gây Cấn', 'null', 'false', 'beginner', '2025-12-06 13:47:56.572007+00');
